mapper.delete:
delete from 
where userId = #{userId, jdbcType=VARCHAR}
      and favoriteId = #{favoriteId, jdbcType=VARCHAR}
      and favoriteType = #{favoriteType, jdbcType=VARCHAR}

mapper.select:
SELECT

FROM

where 1=1

  and userId = #{userId, jdbcType=VARCHAR}


  and favoriteId = #{favoriteId, jdbcType=VARCHAR}


  and favoriteType = #{favoriteType, jdbcType=VARCHAR}

order by lastEditTime desc
