mapper.select:
SELECT * FROM 
where 1=1

  and ArticleId = #{articleId, jdbcType=VARCHAR}


    and CategoryId = #{categoryId, jdbcType=VARCHAR}


  and UserId = #{userId, jdbcType=VARCHAR}



  and Title like #{bind_title}



    and Content like #{bind_content}

AND CreateTime >= #{createTimeStart, jdbcType=TIMESTAMP}
AND CreateTime <=#{createTimeEnd, jdbcType=TIMESTAMP}
AND LastEditTime >= #{lastEditTimeStart, jdbcType=TIMESTAMP}
AND LastEditTime <=#{lastEditTimeEnd, jdbcType=TIMESTAMP}
